import React, { useEffect, useState } from 'react';
import { Table, Spinner } from 'react-bootstrap';
import {BookingAPI} from '../../API';

const ManageBookingsAdmin = () => {
  const [bookings, setBookings] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
      const fetchBookings = async () => {
          try {
              setLoading(true);
              const data = await BookingAPI.GetAllBookings();
              setBookings(data);
          } catch (err) {
              setError('Lỗi khi tải dữ liệu.');
              console.error(err);
          } finally {
              setLoading(false);
          }
      };
      fetchBookings();
  }, []);

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
};

const handleReceive = async (id, isReceived) => {
    try {
        await BookingAPI.UpdateBookingStatus(id, !isReceived);
        setBookings((prev) =>
            prev.map((booking) =>
                booking.id === id ? { ...booking, isReceived: !isReceived } : booking
            )
        );
    } catch (err) {
        setError('Lỗi khi cập nhật trạng thái nhận sân.');
        console.error(err);
    }
};

const filteredBookings = bookings.filter(
    (booking) =>
        booking.displayName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        booking.phoneNumber.includes(searchTerm)
);

return (
    <Container className="mt-4">
        <Row className="mb-3">
            <Col md={8}>
                <h3>Quản Lý Đặt Sân</h3>
            </Col>
            <Col md={4}>
                <Form.Control
                    type="text"
                    placeholder="Tìm kiếm theo tên hoặc SĐT..."
                    value={searchTerm}
                    onChange={handleSearch}
                />
            </Col>
        </Row>

        {error && <Alert variant="danger">{error}</Alert>}

        {loading ? (
            <p>Đang tải dữ liệu...</p>
        ) : (
            <Table striped bordered hover responsive className="table-sm">
                <thead className="table-primary">
                    <tr>
                        <th>#</th>
                        <th>Họ Tên</th>
                        <th>SĐT</th>
                        <th>Chi Tiết Sân</th>
                        <th>Ngày Đặt</th>
                        <th>TT Thanh Toán</th>
                        {role === 'Staff' && <th>Nhận Sân</th>}
                    </tr>
                </thead>
                <tbody>
                    {filteredBookings.map((booking, index) => (
                        <tr key={booking.id}>
                            <td>{index + 1}</td>
                            <td>{booking.displayName}</td>
                            <td>{booking.phoneNumber}</td>
                            <td>
                                {booking.pitchName} - {booking.pitchTypeName}
                            </td>
                            <td>{new Date(booking.bookingDate).toLocaleString('vi-VN')}</td>
                            <td>
                                <span
                                    className={
                                        booking.isPaid
                                            ? 'badge bg-success'
                                            : 'badge bg-warning text-dark'
                                    }
                                >
                                    {booking.isPaid ? 'Đã TT' : 'Chưa TT'}
                                </span>
                            </td>
                            {role === 'Staff' && (
                                <td className="text-center">
                                    <Form.Check
                                        type="checkbox"
                                        checked={booking.isReceived}
                                        onChange={() => handleReceive(booking.id, booking.isReceived)}
                                    />
                                </td>
                            )}
                        </tr>
                    ))}
                </tbody>
            </Table>
        )}
    </Container>
);
}

export default ManageBookingsAdmin;
