import "bootstrap/dist/css/bootstrap.min.css";
import Home from "./Pages/Home";
import Login from './Pages/Login';
import ManagePitchTypes from './Pages/Admin/ManagePitchTypes';
import ManagePitches from './Pages/Admin/ManagePitches';
import ManageBookingsAdmin from './Pages/Admin/ManageBookingsAdmin';
import RevenueReport from './Pages/Admin/RevenueReport';
import CreateStaff from './Pages/Admin/CreateStaff';
import { Routes, Route, Navigate } from 'react-router-dom'; // Loại bỏ Router
import NavigationBar from "./Components/Navbar";
import { AuthProvider } from "./Context/AuthContext";

const ProtectedRoute = ({ role, children }) => {
  const token = localStorage.getItem("token");
  const userRole = localStorage.getItem("role");

  if (!token) return <Navigate to="/login" />;

  if (role && userRole !== role) return <Navigate to="/" />;
  return children;
};

const App = () => {
  return (
    <AuthProvider>
      <NavigationBar />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        {/* Admin Routes */}
        <Route
          path="/manage-pitches-admin"
          element={
            <ProtectedRoute role="Admin">
              <ManagePitches />
            </ProtectedRoute>
          }
        />
        <Route
          path="/manage-pitch-types-admin"
          element={
            <ProtectedRoute role="Admin">
              <ManagePitchTypes />
            </ProtectedRoute>
          }
        />
        <Route
          path="/manage-bookings-admin"
          element={
            <ProtectedRoute role="Admin">
              <ManageBookingsAdmin />
            </ProtectedRoute>
          }
        />
        <Route
          path="/revenue-report-admin"
          element={
            <ProtectedRoute role="Admin">
              <RevenueReport />
            </ProtectedRoute>
          }
        />
        <Route
          path="/create-staff"
          element={
            <ProtectedRoute role="Admin">
              <CreateStaff />
            </ProtectedRoute>
          }
        />
      </Routes>
    </AuthProvider>
  );
};

export default App;
