import React, { useState, useEffect } from "react";
import { Button, Modal, Form, Table, Spinner, Alert } from 'react-bootstrap';
import { PitchTypeAPI } from "../../API";
import "../../CSS/Admin/ManagePitchTypeAdmin.css";

const ManagePitchType = () => {
  const [pitchTypes, setPitchTypes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const [showModal, setShowModal] = useState(false);
  const [formData, setFormData] = useState({ name: '', price: '', limitPerson: '', image: [] });
  const [editingId, setEditingId] = useState(null);

  const fetchPitchTypes = async () => {
    setLoading(true);
    try {
      const data = await PitchTypeAPI.GetAll();
      setPitchTypes(data);
    } catch (err) {
      setError(err.message || 'Failed to fetch pitch types.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPitchTypes();
  }, []);

  const handleInputChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'image') {
      setFormData({ ...formData, image: files ? Array.from(files) : [] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      if (editingId) {
        await PitchTypeAPI.UpdatePitchType(editingId, formData);
      } else {
        await PitchTypeAPI.CreatePitchType(formData);
      }
      setShowModal(false);
      fetchPitchTypes();
    } catch (err) {
      setError(err.message || 'Failed to save pitch type.');
    } finally {
      setLoading(false);
      setFormData({ name: '', price: '', limitPerson: '', image: [] });
      setEditingId(null);
    }
  };

  const handleEdit = (pitchType) => {
    setEditingId(pitchType.id);
    setFormData({
      name: pitchType.name,
      price: pitchType.price,
      limitPerson: pitchType.limitPerson,
      image: [],
    });
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this pitch type?')) {
      setLoading(true);
      try {
        await PitchTypeAPI.DeletePitchType(id);
        fetchPitchTypes();
      } catch (err) {
        setError(err.message || 'Failed to delete pitch type.');
      } finally {
        setLoading(false);
      }
    }
  };

  return (
    <div className="container mt-4">
      <h2>Quản lý loại sân</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      {loading && <Spinner animation="border" className="my-3" />}

      <Button onClick={() => setShowModal(true)} className="mb-3">
        Thêm mới
      </Button>

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>STT</th>
            <th>Tên</th>
            <th>Giá</th>
            <th>Số lượng người tối đa</th>
            <th>Hành động</th>
          </tr>
        </thead>
        <tbody>
          {pitchTypes.map((pitchType, index) => (
            <tr key={pitchType.id}>
              <td>{index + 1}</td>
              <td>{pitchType.name}</td>
              <td>{pitchType.price}</td>
              <td>{pitchType.limitPerson}</td>
              <td>
                <Button variant="warning" size="sm" onClick={() => handleEdit(pitchType)}>
                  Edit
                </Button>{' '}
                <Button variant="danger" size="sm" onClick={() => handleDelete(pitchType.id)}>
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{editingId ? 'Edit Pitch Type' : 'Add New Pitch Type'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Price</Form.Label>
              <Form.Control
                type="number"
                name="price"
                value={formData.price}
                onChange={handleInputChange}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Limit Person</Form.Label>
              <Form.Control
                type="number"
                name="limitPerson"
                value={formData.limitPerson}
                onChange={handleInputChange}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Image</Form.Label>
              <Form.Control
                type="file"
                name="image"
                onChange={handleInputChange}
                multiple
                accept="image/*"
              />
            </Form.Group>

            <Button variant="primary" type="submit" disabled={loading}>
              {loading ? 'Saving...' : 'Save'}
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default ManagePitchType;
