import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:8000/api',
  headers: {
    'Content-Type': 'application/json',
  },
});

api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
}, (error) => Promise.reject(error));

// Kiểm tra role người dùng (Admin)
const isAdmin = () => {
  try {
    const token = localStorage.getItem('token');
    if (!token) return false;

    const payload = JSON.parse(atob(token.split('.')[1])); // Decode JWT payload
    const role = payload["http://schemas.microsoft.com/ws/2008/06/identity/claims/role"];
    return role === 'Admin';
  } catch (error) {
    console.error('Error decoding token:', error);
    return false;
  }
};

const AccountAPI = {
  login: async (email, password) => {
    try {
      const response = await api.post('/Account/login', { email, password });
      const { token, role, username } = response.data;

      // Lưu thông tin vào localStorage
      localStorage.setItem('token', token);
      localStorage.setItem('role', role);
      localStorage.setItem('username', username);

      console.log("Username: ", username + "\nToken: ", token + "\nRole: ", role);

      return response.data;
    } catch (error) {
      console.error('Login failed:', error);
      throw error.response?.data || new Error('Login failed');
    }
  },
};

const PitchTypeAPI = {
  GetAll: async () => {
    try {
      if (!isAdmin()) {
        throw new Error('Không có quyền: Chỉ tài khoản Admin mới có thể truy cập.');
      }

      localStorage.getItem('token');
      localStorage.getItem('role');

      const response = await api.get('/PitchType');
      return response.data;
      console.log(api.get('/PitchType'))
    } catch (err) {
      console.error('Lỗi khi lấy danh sách loại sân: ', err);
      throw err.response?.data || new Error('Không thể lấy danh sách loại sân.');
    }
  },

  CreatePitchType: async (data) => {
    try {
      if (!isAdmin()) {
        throw new Error('Không có quyền: Chỉ tài khoản Admin mới có thể thêm loại sân.');
      }
      const response = await api.post('/PitchType', data);
      return response.data;
    } catch (error) {
      console.error('Lỗi khi thêm loại sân:', error);
      throw error.response?.data || new Error('Không thể thêm loại sân.');
    }
  },

  UpdatePitchType: async (id, data) => {
    try {
      if (!isAdmin()) {
        throw new Error('Không có quyền: Chỉ tài khoản Admin mới có thể sửa loại sân.');
      }
      const response = await api.put(`/PitchType/${id}`, data);
      return response.data;
    } catch (error) {
      console.error('Lỗi khi sửa loại sân:', error);
      throw error.response?.data || new Error('Không thể sửa loại sân.');
    }
  },

  DeletePitchType: async (id) => {
    try {
      if (!isAdmin()) {
        throw new Error('Không có quyền: Chỉ tài khoản Admin mới có thể xóa loại sân.');
      }
      await api.delete(`/PitchType/${id}`);
      return { success: true };
    } catch (error) {
      console.error('Lỗi khi xóa loại sân:', error);
      throw error.response?.data || new Error('Không thể xóa loại sân.');
    }
  },
};

export { AccountAPI, PitchTypeAPI };
