import "bootstrap/dist/css/bootstrap.min.css";
import Home from "./Pages/Home";
import Login from '../src/Pages/Login';
import ManagePitchTypes from '../src/Pages/Admin/ManagePitchTypes';
import ManagePitches from '../src/Pages/Admin/ManagePitches';
import ManageBookingsAdmin from '../src/Pages/Admin/ManageBookingsAdmin';
import RevenueReport from '../src/Pages/Admin/RevenueReport';
import CreateStaff from '../src/Pages/Admin/CreateStaff';
import { Router, Routes, Route, Navigate } from 'react-router-dom';
import NavigationBar from "./Components/Navbar";
import { AuthProvider } from "./Context/AuthContext";

const ProtectedRoute = ({ role, children }) => {
  const token = localStorage.getItem("token");
  const userRole = localStorage.getItem("role");

  if (!token) return <Navigate to="/login" />

  if (role & userRole !== role) return <Navigate to="/" />
  return children;
}

const App = () => {
  return (
    <AuthProvider>
      <Router>
        <NavigationBar />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login />} />

          {/* Admin Routes */}
          <Route
            path="/manage-pitches-admin"
            element={
              <ProtectedRoute role="Admin">
                <ManagePitches />
              </ProtectedRoute>
            }
          />
          <Route
            path='/manage-pitch-types-admin'
            element={
              <ProtectedRoute>
                <ManagePitchTypes />
              </ProtectedRoute>
            }
          />
          <Route
            path='/manage-bookings-admin'
            element={
              <ProtectedRoute>
                <ManageBookingsAdmin />
              </ProtectedRoute>
            }
          />
          <Route
            path='/revenue-report-admin'
            element={
              <ProtectedRoute>
                <RevenueReport />
              </ProtectedRoute>
            }
          />
          <Route
            path='/create-staff'
            element={
              <ProtectedRoute>
                <CreateStaff />
              </ProtectedRoute>
            }
          />

        </Routes>
      </Router>
    </AuthProvider>
  );
}

export default App;
