import React, { useState, useEffect } from "react";
import { Button, Table, Modal, Form, Alert } from "react-bootstrap";
import { PitchTypeAPI } from "../../API";

const ManagePitchType = () => {
  const [pitchTypes, setPitchTypes] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [currentPitchType, setCurrentPitchType] = useState(null);
  const [formData, setFormData] = useState({
    name: "",
    price: "",
    limitPerson: "",
    images: [], // Thay đổi để hỗ trợ nhiều hình ảnh
  });
  const [error, setError] = useState(null);

  const fetchPitchTypes = async () => {
    try {
      const data = await PitchTypeAPI.GetAll();
      setPitchTypes(data);
    } catch (err) {
      setError(err.message || "Lỗi khi tải danh sách loại sân.");
    }
  };

  useEffect(() => {
    fetchPitchTypes();
  }, []);

  const handleInputChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "images") {
      setFormData({ ...formData, [name]: [...files] }); // Lưu nhiều file
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleShowModal = (pitchType = null) => {
    if (pitchType) {
      setCurrentPitchType(pitchType);
      setFormData({
        name: pitchType.name,
        price: pitchType.price,
        limitPerson: pitchType.limitPerson,
        images: [], // Không hiển thị hình ảnh cũ
      });
    } else {
      setCurrentPitchType(null);
      setFormData({
        name: "",
        price: "",
        limitPerson: "",
        images: [],
      });
    }
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setError(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (formData.images.length > 0) {
        // Upload nhiều hình ảnh
        const formDataImages = new FormData();
        formData.images.forEach((image) => formDataImages.append("imageFiles", image));

        const uploadResponse = await PitchTypeAPI.UploadImages(formDataImages);
        formData.imagePaths = uploadResponse.FilePaths; // API trả về danh sách đường dẫn
      }

      if (currentPitchType) {
        await PitchTypeAPI.UpdatePitchType(currentPitchType.id, formData);
      } else {
        await PitchTypeAPI.CreatePitchType(formData);
      }
      fetchPitchTypes();
      handleCloseModal();
    } catch (err) {
      setError(err.message || "Lỗi khi lưu loại sân.");
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Bạn có chắc chắn muốn xóa loại sân này?")) {
      try {
        await PitchTypeAPI.DeletePitchType(id);
        fetchPitchTypes();
      } catch (err) {
        alert(err.message || "Lỗi khi xóa loại sân.");
      }
    }
  };

  return (
    <div className="container mt-4">
      <h2>Quản Lý Loại Sân</h2>
      <Button onClick={() => handleShowModal()} variant="primary" className="mb-3">
        Thêm Loại Sân
      </Button>

      {error && <Alert variant="danger">{error}</Alert>}

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>#</th>
            <th>Tên Loại Sân</th>
            <th>Giá</th>
            <th>Số Người Tối Đa</th>
            <th>Hình Ảnh</th>
            <th>Hành Động</th>
          </tr>
        </thead>
        <tbody>
          {pitchTypes.map((pitchType, index) => (
            <tr key={pitchType.id}>
              <td>{index + 1}</td>
              <td>{pitchType.name}</td>
              <td>{pitchType.price}</td>
              <td>{pitchType.limitPerson}</td>
              <td>
                {/* Hiển thị nhiều ảnh */}
                {pitchType.imagePaths?.map((path, idx) => (
                  <img
                    key={idx}
                    src={path}
                    alt={`${pitchType.name}-${idx}`}
                    style={{ width: "100px", height: "auto", marginRight: "10px" }}
                  />
                ))}
              </td>
              <td>
                <Button
                  variant="warning"
                  className="me-2"
                  onClick={() => handleShowModal(pitchType)}
                >
                  Sửa
                </Button>
                <Button variant="danger" onClick={() => handleDelete(pitchType.id)}>
                  Xóa
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Modal Thêm/Sửa */}
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>{currentPitchType ? "Sửa Loại Sân" : "Thêm Loại Sân"}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3">
              <Form.Label>Tên Loại Sân</Form.Label>
              <Form.Control
                type="text"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Giá</Form.Label>
              <Form.Control
                type="number"
                name="price"
                value={formData.price}
                onChange={handleInputChange}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Số Người Tối Đa</Form.Label>
              <Form.Control
                type="number"
                name="limitPerson"
                value={formData.limitPerson}
                onChange={handleInputChange}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Hình Ảnh</Form.Label>
              <Form.Control
                type="file"
                name="images"
                accept="image/*"
                multiple // Cho phép chọn nhiều file
                onChange={handleInputChange}
              />
            </Form.Group>
            <Button variant="primary" type="submit">
              Lưu
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default ManagePitchType;
