import React, { useEffect, useState } from 'react';
import { Table, Spinner, Alert } from 'react-bootstrap';
import {BookingAPI} from '../../API';

const ManageBookingsAdmin = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [role, setRole] = useState('');

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const data = await BookingAPI.GetAllBookings();
        console.log("Booking API: ", BookingAPI);
        setBookings(data);
      } catch (err) {
        setError(err.message || 'Không thể tải danh sách đặt sân.');
      } finally {
        setLoading(false);
      }
    };

    fetchBookings();
  }, []);

  if (loading) {
    return <Spinner animation="border" />;
  }

  if (error) {
    return <Alert variant="danger">{error}</Alert>;
  }

  return (
    <div>
      <h1>Quản Lý Đặt Sân</h1>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>STT</th>
            <th>Họ Tên (Customer)</th>
            <th>Chi Tiết Sân</th>
            <th>Ngày Đặt Sân</th>
            <th>Trạng Thái Thanh Toán</th>
          </tr>
        </thead>
        <tbody>
          {bookings.map((booking, index) => (
            <tr key={booking.id}>
              <td>{index + 1}</td>
              <td>{booking.customerName}</td>
              <td>{`${booking.pitchName} - ${booking.pitchTypeName}`}</td>
              <td>{new Date(booking.bookingDate).toLocaleString('vi-VN')}</td>
              <td>{booking.paymentStatus}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default ManageBookingsAdmin;
