import React, { useEffect, useState } from 'react';
import { Button, Table, Modal, Form, Alert } from 'react-bootstrap';
import { PitchTypeAPI } from '../../Ap';

const ManagePitchTypes = () => {
  const [pitchTypes, setPitchTypes] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [currentPitchType, setCurrentPitchType] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Lấy danh sách loại sân
  const fetchPitchTypes = async () => {
    try {
      const data = await PitchTypeAPI.GetAll();
      setPitchTypes(data);
    } catch (err) {
      setError(err.message || 'Không thể tải danh sách loại sân.');
    }
  };
  // Xử lý mở modal (thêm mới hoặc chỉnh sửa)
  const handleShowModal = (pitchType = null) => {
    setCurrentPitchType(pitchType);
    setShowModal(true);
    setError('');
    setSuccess('');
  };

  // Xử lý đóng modal
  const handleCloseModal = () => {
    setCurrentPitchType(null);
    setShowModal(false);
  };

  // Xử lý thêm hoặc cập nhật loại sân
  const handleSave = async () => {
    try {
      if (currentPitchType.id) {
        // Cập nhật loại sân
        await PitchTypeAPI.UpdatePitchType(currentPitchType.id, currentPitchType);
        setSuccess('Cập nhật loại sân thành công.');
      } else {
        // Thêm mới loại sân
        await PitchTypeAPI.CreatePitchType(currentPitchType);
        setSuccess('Thêm loại sân thành công.');
      }
      fetchPitchTypes(); // Làm mới danh sách loại sân
      handleCloseModal();
    } catch (err) {
      setError(err.message || 'Không thể lưu loại sân.');
    }
  };

  // Xử lý xóa loại sân
  const handleDelete = async (id) => {
    if (window.confirm('Bạn có chắc chắn muốn xóa loại sân này không?')) {
      try {
        await PitchTypeAPI.DeletePitchType(id);
        setSuccess('Xóa loại sân thành công.');
        fetchPitchTypes();
      } catch (err) {
        setError(err.message || 'Không thể xóa loại sân.');
      }
    }
  };

  // Cập nhật thông tin loại sân trong modal
  const handleChange = (e) => {
    const { name, value } = e.target;
    setCurrentPitchType({ ...currentPitchType, [name]: value });
  };

  // Lấy dữ liệu khi component được mount
  useEffect(() => {
    fetchPitchTypes();
  }, []);

  return (
    <div className="container mt-4">
      <h1>Quản Lý Loại Sân</h1>

      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}

      <Button variant="primary" onClick={() => handleShowModal()}>
        Thêm Loại Sân
      </Button>

      <Table striped bordered hover className="mt-3">
        <thead>
          <tr>
            <th>#</th>
            <th>Tên</th>
            <th>Giá</th>
            <th>Số Người Tối Đa</th>
            <th>Hành Động</th>
          </tr>
        </thead>
        <tbody>
          {pitchTypes.map((pitchType, index) => (
            <tr key={pitchType.id}>
              <td>{index + 1}</td>
              <td>{pitchType.name}</td>
              <td>{pitchType.price.toLocaleString('vi-VN', { style: 'currency', currency: 'VND' })}</td>
              <td>{pitchType.limitPerson}</td>
              <td>
                <Button
                  variant="warning"
                  className="me-2"
                  onClick={() => handleShowModal(pitchType)}
                >
                  Sửa
                </Button>
                <Button variant="danger" onClick={() => handleDelete(pitchType.id)}>
                  Xóa
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Modal Thêm/Sửa */}
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>{currentPitchType?.id ? 'Cập Nhật Loại Sân' : 'Thêm Loại Sân'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Tên Loại Sân</Form.Label>
              <Form.Control
                type="text"
                name="name"
                value={currentPitchType?.name || ''}
                onChange={handleChange}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Giá</Form.Label>
              <Form.Control
                type="number"
                name="price"
                value={currentPitchType?.price || ''}
                onChange={handleChange}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Số Người Tối Đa</Form.Label>
              <Form.Control
                type="number"
                name="limitPerson"
                value={currentPitchType?.limitPerson || ''}
                onChange={handleChange}
                required
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Hủy
          </Button>
          <Button variant="primary" onClick={handleSave}>
            Lưu
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default ManagePitchTypes;
