import React, { useEffect, useState } from 'react';
import { Button, Table, Modal, Form, Alert, Spinner } from 'react-bootstrap';
import { PitchTypeAPI } from '../../API';

const ManagePitchTypes = () => {
  const [pitchTypes, setPitchTypes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [currentPitchType, setCurrentPitchType] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [previewImage, setPreviewImage] = useState(null);

  const fetchPitchTypes = async () => {
    setLoading(true);
    try {
      const data = await PitchTypeAPI.GetAll();
      setPitchTypes(data);
    } catch (err) {
      setError(err.response?.data?.message || 'Không thể tải danh sách loại sân.');
    } finally {
      setLoading(false);
    }
  };

  const handleShowModal = (pitchType = null) => {
    setCurrentPitchType(
      pitchType || { name: '', price: '', limitPerson: '', imageFile: null, imageUrl: '' }
    );
    setPreviewImage(pitchType?.imageUrl || null);
    setShowModal(true);
    setError('');
    setSuccess('');
  };

  const handleCloseModal = () => {
    setCurrentPitchType(null);
    setShowModal(false);
    setPreviewImage(null);
  };

  const handleSave = async () => {
    if (!currentPitchType.name || !currentPitchType.price || !currentPitchType.limitPerson) {
      setError('Vui lòng điền đầy đủ thông tin.');
      return;
    }

    try {
      const formData = new FormData();
      formData.append('name', currentPitchType.name.trim());
      formData.append('price', currentPitchType.price);
      formData.append('limitPerson', currentPitchType.limitPerson);

      if (currentPitchType.imageFile) {
        formData.append('imageFile', currentPitchType.imageFile);
      }

      if (currentPitchType.id) {
        await PitchTypeAPI.UpdatePitchType(currentPitchType.id, formData);
        setSuccess('Cập nhật loại sân thành công!');
      } else {
        await PitchTypeAPI.CreatePitchType(formData);
        setSuccess('Thêm loại sân thành công!');
      }

      fetchPitchTypes();
      handleCloseModal();
    } catch (err) {
      setError(err.response?.data?.message || 'Không thể lưu loại sân.');
    }
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setCurrentPitchType((prev) => ({ ...prev, imageFile: file }));
      setPreviewImage(URL.createObjectURL(file));
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Bạn có chắc chắn muốn xóa loại sân này không?')) {
      try {
        await PitchTypeAPI.DeletePitchType(id);
        setSuccess('Xóa loại sân thành công.');
        fetchPitchTypes();
      } catch (err) {
        setError(err.response?.data?.message || 'Không thể xóa loại sân.');
      }
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCurrentPitchType((prev) => ({ ...prev, [name]: value }));
  };

  useEffect(() => {
    fetchPitchTypes();
  }, []);

  return (
    <div className="container mt-4">
      <h1>Quản Lý Loại Sân</h1>

      {error && <Alert variant="danger" onClose={() => setError('')} dismissible>{error}</Alert>}
      {success && <Alert variant="success" onClose={() => setSuccess('')} dismissible>{success}</Alert>}

      <Button variant="primary" onClick={() => handleShowModal()} className="mb-3">
        Thêm Loại Sân
      </Button>

      {loading ? (
        <div className="text-center">
          <Spinner animation="border" />
        </div>
      ) : (
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>#</th>
              <th>Tên</th>
              <th>Giá</th>
              <th>Số Người Tối Đa</th>
              <th>Hình Ảnh</th>
              <th>Hành Động</th>
            </tr>
          </thead>
          <tbody>
            {pitchTypes.map((pitchType, index) => (
              <tr key={pitchType.id}>
                <td>{index + 1}</td>
                <td>{pitchType.name}</td>
                <td>{pitchType.price.toLocaleString('vi-VN', { style: 'currency', currency: 'VND' })}</td>
                <td>{pitchType.limitPerson}</td>
                <td>
                  {pitchType.imageUrl ? (
                    <img src={pitchType.imageUrl} alt="Loại sân" style={{ width: '100px', height: '60px' }} />
                  ) : (
                    'Không có ảnh'
                  )}
                </td>
                <td>
                  <Button variant="warning" className="me-2" onClick={() => handleShowModal(pitchType)}>
                    Sửa
                  </Button>
                  <Button variant="danger" onClick={() => handleDelete(pitchType.id)}>
                    Xóa
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}

      <Modal show={showModal} onHide={handleCloseModal} backdrop="static">
        <Modal.Header closeButton>
          <Modal.Title>{currentPitchType?.id ? 'Cập Nhật Loại Sân' : 'Thêm Loại Sân'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Tên Loại Sân</Form.Label>
              <Form.Control
                type="text"
                name="name"
                value={currentPitchType?.name || ''}
                onChange={handleChange}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Giá</Form.Label>
              <Form.Control
                type="number"
                name="price"
                value={currentPitchType?.price || ''}
                onChange={handleChange}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Số Người Tối Đa</Form.Label>
              <Form.Control
                type="number"
                name="limitPerson"
                value={currentPitchType?.limitPerson || ''}
                onChange={handleChange}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Hình Ảnh</Form.Label>
              <Form.Control type="file" accept="image/*" onChange={handleFileUpload} />
              {previewImage && (
                <img src={previewImage} alt="Preview" className="mt-3" style={{ width: '100%', height: 'auto' }} />
              )}
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Hủy
          </Button>
          <Button variant="primary" onClick={handleSave}>
            Lưu
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default ManagePitchTypes;
